using System;
using System.Net;
using EPiServer;
using EPiServer.Logging;
using RestSharp;
using TRM.Web.Extentions;

namespace TRM.Web.Services.HtmlToPdf
{
    public class RestPackHtmlToPdf : ITrmHtmlToPdf
    {
        private readonly ILogger _logger = LogManager.GetLogger(typeof(ITrmHtmlToPdf));

        private readonly IContentLoader _contentLoader;

        public RestPackHtmlToPdf(IContentLoader contentLoader)
        {
            _contentLoader = contentLoader;
        }

        public byte[] ConvertHtmlToPdf(string htmlString, string footerText)
        {
            try
            {
                var startPage = _contentLoader.GetAppropriateStartPageForSiteSpecificProperties();
                if(startPage == null || string.IsNullOrEmpty(startPage.RestPackApiUrl) || string.IsNullOrEmpty(startPage.RestPackAccessToken))
                    throw new ArgumentException("RestPack's setting must be setting in Start Page");

                IRestRequest request = new RestRequest(Method.POST);
                request.AddHeader("x-access-token", startPage.RestPackAccessToken);

                var postData = new
                {
                    html = htmlString,
                    privacy = "true",
                    cache = "false",
                    pdf_page = "A4",
                    shutter = "#shutter.close",
                    pdf_orientation = "landscape",
                    pdf_margins = "155px 0 80px 0",
                    pdf_header = "<img style=\"width:100%;\" src=\"data:image/png;base64, \"/>",
                    pdf_footer = $"<div style=\"padding:10px;font-size:8px;font-family: Calibri,Candara,Segoe,Segoe UI,Optima,Arial,sans-serif;vertical-align:middle;\" >" +
                                 $"<div style=\"float:left;width:33%;\">{DateTime.Now:d-MMM-yyyy}</div>" +
                                 $"<div style=\"float:left;width:33%;text-align:center;\">{footerText}</div>" +
                                 $"<div style=\"float:right;width:33%;text-align:right;\">Page <span class=\"pageNumber\"></span> of <span class=\"totalPages\"></span></div></div>"
                };
                request.AddJsonBody(postData);

                IRestClient client = new RestClient(startPage.RestPackApiUrl);
                var response = client.Execute(request);

                if(response.IsSuccessful) return response.RawBytes;

                _logger.Log(Level.Error, response.Content);
            }
            catch (WebException wex)
            {
                _logger.Log(Level.Error, wex.StackTrace);
            }
            catch (Exception err)
            {
                _logger.Log(Level.Error, err.Message);
                _logger.Log(Level.Error, err.StackTrace);
            }

            return null;
        }
    }
}